INSERT INTO `wp_posts` VALUES (627,1,'2013-05-28 20:01:53','2013-05-29 00:01:53','<p class=\"p1\">On some portable models, serious crashes can sometimes cause the clock to reset. The crashing may be a sign of a problem, but the clock changing is normal. If a portable computer’s battery is completely discharged, the clock may also reset.</p>','The date on my computer keeps going back to 1-1-1904, or 8-27-1956, or 1969, or 1970.','','publish','open','open','','the-date-on-my-computer-keeps-going-back-to-1-1-1904-or-8-27-1956-or-1969-or-1970','','','2013-06-18 17:10:03','2013-06-18 21:10:03','',0,'http://localhost/tekfaqs/?p=627',66,'post','',0);
