INSERT INTO `wp_posts` VALUES (268,1,'2013-05-23 18:38:05','2013-05-23 22:38:05','<p class=\"p1\">Tekserve not only repairs Apple computers, but we rent and sell them too. Whatever you need, we’ll keep you working with a low-cost rental or by transferring your information to a new Mac. You may reach our rentals department by email at <a href=\"mailto:rentals@tekserve.com\">rentals@tekserve.com</a>, by calling (212) 929-3645 ext. 476, or by <a href=\"http://www.tekserve.com/rentals/request-rental\">requesting a quote online</a>.</p>','My Mac just died, and I need a computer ASAP!','','publish','open','open','','my-mac-just-died-and-i-need-a-computer-asap','','','2013-05-28 16:08:14','2013-05-28 20:08:14','',0,'http://localhost/tekfaqs/?p=268',29,'post','',0);
