INSERT INTO `wp_posts` VALUES (563,1,'2013-05-28 14:03:36','2013-05-28 18:03:36','<p class=\"p1\"><strong>Use a power outlet:</strong> Use the power adapter and the cable that came with your iDevice. You can connect the dock end of the cable to a dock or directly to the device.</p>\r\n<p class=\"p1\"><strong>Use a computer:</strong> Use the cable and connect your iDevice to a USB 2.0 or 3.0 port. The ports on older computers, most keyboards, and USB hubs (unless they&rsquo;re separately powered) don&rsquo;t provide enough juice to charge your iDevice.</p>','How do I charge the battery?','','inherit','open','open','','145-revision-4','','','2013-05-28 14:03:36','2013-05-28 18:03:36','',145,'http://localhost/tekfaqs/?p=563',0,'revision','',0);
