INSERT INTO `wp_posts` VALUES (543,1,'2013-05-23 15:25:56','2013-05-23 19:25:56','<p class=\"p1\">Macintosh portable computers may go into &ldquo;deep sleep&rdquo; mode and appear completely dead. Wake them up by momentarily pressing the power button (only press briefly, don&rsquo;t keep it held down).</p>','What if my computer won\'t wake from sleep?','','inherit','open','open','','195-revision-3','','','2013-05-23 15:25:56','2013-05-23 19:25:56','',195,'http://localhost/tekfaqs/?p=543',0,'revision','',0);
