INSERT INTO `wp_posts` VALUES (629,1,'2013-05-28 20:04:57','2013-05-29 00:04:57','<p class=\"p1\">Log in to your account by clicking on your email address in the iTunes store (or click the <em>Sign In</em> button). Under the Store menu choose <em>Authorize This Computer</em>. You can authorize up to five different computers to play your iTunes purchases. Don’t worry if you forgot to deauthorize your computer before the repair. You can reset your authorizations once a year. See <a href=\"http://www.tekserve.com/faq/1420\">tekserve.com/faq/1420</a> for details.</p>','My computer was just repaired and now I can\'t play files I purchased on iTunes.','','publish','open','open','','my-computer-was-just-repaired-and-now-i-cant-play-files-i-purchased-on-itunes','','','2013-05-28 20:04:57','2013-05-29 00:04:57','',0,'http://localhost/tekfaqs/?p=629',67,'post','',0);
